import React, { Component } from 'react';

class BubbleSortSnippet extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dragging: false, // State variable to track if the snippet is being dragged
            mouseX: 0, // State variable to store the current x-coordinate of the cursor
            mouseY: 0, // State variable to store the current y-coordinate of the cursor
            offsetX: 0, // State variable to store the initial x-coordinate of the snippet
            offsetY: 0, // State variable to store the initial y-coordinate of the snippet
        };
    }

    // Event handler for mouse down event
    handleMouseDown = (e) => {
        // Update state variables to start dragging the snippet
        this.setState({
            dragging: true,
            mouseX: e.clientX,
            mouseY: e.clientY,
            offsetX: this.state.offsetX,
            offsetY: this.state.offsetY,
        });

        // Add event listeners for mouse move and mouse up events
        document.addEventListener('mousemove', this.handleMouseMove);
        document.addEventListener('mouseup', this.handleMouseUp);
    }

    // Event handler for mouse move event
    handleMouseMove = (e) => {
        // Update the position of the snippet based on the cursor coordinates
        if (this.state.dragging) {
            const deltaX = e.clientX - this.state.mouseX;
            const deltaY = e.clientY - this.state.mouseY;
            this.setState({
                offsetX: this.state.offsetX + deltaX,
                offsetY: this.state.offsetY + deltaY,
                mouseX: e.clientX,
                mouseY: e.clientY,
            });
        }
    }

    // Event handler for mouse up event
    handleMouseUp = () => {
        // Update state variables to stop dragging the snippet
        this.setState({ dragging: false });

        // Remove event listeners for mouse move and mouse up events
        document.removeEventListener('mousemove', this.handleMouseMove);
        document.removeEventListener('mouseup', this.handleMouseUp);
    }

    render() {
        const lines = [
            'function bubbleSort(arr) {',
            '    let len = arr.length;',
            '    for (let i = 0; i < len - 1; i++) {',
            '        for (let j = 0; j < len - i - 1; j++) {',
            '            if (arr[j] > arr[j + 1]) {',
            '                let temp = arr[j];',
            '                arr[j] = arr[j + 1];',
            '                arr[j + 1] = temp;',
            '            }',
            '        }',
            '    }',
            '    return arr;',
            '}',
        ];
        const { offsetX, offsetY } = this.state;

        return (
            <div
                className="bubble-sort-snippet"
                style={{ left: offsetX, top: offsetY }}
                onMouseDown={this.handleMouseDown} // Add event listener for mouse down event
            >
                {/* Render the content of the snippet */}
                {/* ... */}
            </div>
        );
    }
}

export default BubbleSortSnippet;
