import React, {Component} from 'react';
import DiscreteSlider from "./slider";
import SimpleSelect from "./simpleSelect";
import RangeSlider from "./doubleSlider";
import SwitchLabels from "./formControlLabel";
import InputComponent from "./InputComponent";


class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rectangles: [], // Store rectangles in component state
            inputValues: "" // Initialize inputValues in state
        };
    }

    handleInputChange = (name, value) => {
        // Update input values in component state
        this.setState({ inputValues: value });
    }

    handleCreate = () => {
        // Access input values from component state
        const { inputValues } = this.state;
        const values = inputValues.split(","); // Split input values by commas

        // Use input values to create rectangles or perform other action
        console.log("Input Values: ", values);

        // Example: Create a rectangle object and add it to rectangles array in state
        const rectangle = { width: values[0], height: values[1], color: values[2] };
        this.setState(prevState => ({ rectangles: [...prevState.rectangles, rectangle] }));
    }
    render() {
        return (
            <nav className="nav alert-dark">
                <button
                    className='btn btn-secondary m-2'
                    onClick={this.props.onRandomize}
                    disabled={this.props.disable}
                    style={this.isClickable()}
                >
                    Randomize
                </button>

                <RangeSlider
                    disable={this.props.disable}
                />
                <DiscreteSlider
                    default={20}
                    min={10}
                    max={100}
                    step={10}
                    title="Numbers"
                    onCountChange={this.props.onCountChange}
                    disable={this.props.disable}
                />
                <DiscreteSlider
                    default={50}
                    min={10}
                    max={100}
                    step={1}
                    title="Speed"
                    onCountChange={this.props.onSpeedChange}
                    disable={false}
                />
                <SimpleSelect
                    pos={0}
                    onAlgoChanged={this.props.onAlgoChanged}
                />
                <SwitchLabels
                    disable={this.props.disable}
                    onDoubleChange={this.props.onDoubleChange}
                />
                <SimpleSelect
                    pos={1}
                    onAlgoChanged={this.props.onAlgoChanged}
                />

                <button
                    className='btn btn-warning btn-lg '
                    onClick={this.props.onViusalize}
                    disabled={this.props.disable}
                    style={this.isClickable()}
                >
                    Visualize
                </button>
                <InputComponent
                    onInputChange={this.handleInputChange} // Pass down an event handler to receive input values
                />
                <button onClick={this.handleCreate}>Create</button> {/* Add click event handler */}


            </nav>
        );
    }
    isClickable = () =>{
        if( this.props.disable){
            return {cursor: "not-allowed"};
        } else{
            return {};
        }
    }
}


export default Menu;
