import React, {Component} from 'react';
import DiscreteSlider from "./slider";
import SimpleSelect from "./simpleSelect";
import SwitchLabels from "./formControlLabel";
import InputComponent from "./InputComponent";
import BubbleSortVisualizer from "./BubbleSortVisualizer";

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showExplanation: false // Add a state to toggle the explanation
        };
    }

    toggleExplanation = () => {
        this.setState(prevState => ({
            showExplanation: !prevState.showExplanation
        }));
    }

    render() {
        return (

            <nav className="nav alert-dark">
                <BubbleSortVisualizer/>
                {/* Add a button to toggle the explanation */}
                <button
                                        className='btn btn-secondary m-2'
                                        onClick={this.props.onRandomize}
                                        disabled={this.props.disable}
                                        style={this.isClickable()}
                                    >
                                        Randomize
                                    </button>
                                    <DiscreteSlider
                                        default={20}
                                        min={10}
                                        max={100}
                                        step={10}
                                        title="Numbers"
                                        onCountChange={this.props.onCountChange}
                                        disable={this.props.disable}
                                    />
                                    <DiscreteSlider
                                        default={30}
                                        min={10}
                                        max={100}
                                        step={1}
                                        title="Speed"
                                        onCountChange={this.props.onSpeedChange}
                                        disable={false}
                                    />
                                    <SimpleSelect
                                        pos={0}
                                        onAlgoChanged={this.props.onAlgoChanged}
                                    />
                                    <SwitchLabels
                                        disable={this.props.disable}
                                        onDoubleChange={this.props.onDoubleChange}
                                    />
                                    <SimpleSelect
                                        pos={1}
                                        onAlgoChanged={this.props.onAlgoChanged}
                                    />

                                    <button
                                        className='btn btn-warning btn-lg '
                                        onClick={this.props.onViusalize}
                                        disabled={this.props.disable}
                                        style={this.isClickable()}
                                    >
                                        Visualize
                                    </button>
                                    <InputComponent
                                        onInputChange={this.handleInputChange} // Pass down an event handler to receive input values
                                    />
                                    <button className='btn btn-secondary m-2' onClick={this.props.handleCreate}
                                            disabled={this.props.disable}
                                            style={this.isClickable()}
                                    >
                                        Create
                                    </button> {/* Add click event handler */}




                <button
                    className='btn btn-info m-2'
                    onClick={this.toggleExplanation}
                    style={{float: 'right'}}
                >
                    {this.state.showExplanation ? 'Hide Explanation' : 'Show Explanation'}
                </button>

                {/* Render the explanation if showExplanation state is true */}
                {this.state.showExplanation && (
                    <div className="alert alert-info m-2">
                        <h4>Working Visualizer Explanation</h4>
                        <p>
                            Add your explanation of how the visualizer works here. You can provide details about the
                            algorithms used, the data being visualized, and any other relevant information to help users
                            understand how the visualizer works.
                        </p>
                    </div>
                )}

                {/* Rest of the component's JSX */}
                {/* ... */}
            </nav>

        );
    }

    isClickable = () => {
        if (this.props.disable) {
            return {cursor: "not-allowed"};
        } else {
            return {};
        }
    }
}

export default Menu;
